import React from 'react';
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import InputIcon from "@material-ui/icons/Input";

import Logo from './LOGO.png'
import { Link, useHistory } from "react-router-dom";
const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            minHeight: 70,
            [theme.breakpoints.down("sm")]: {
                paddingLeft: 8,
                paddingRight: 8,
                minHeight: 56,
            },
            marginLeft: 50,
        },
        mainAppBar: {
            background: "#FFFFFF",
            boxShadow: "0px 2px 5px 0px rgba(0, 0, 0, 0.1)",
            zIndex: theme.zIndex.drawer + 1,

        },
        logo: {
            width: 80,
            marginRight: 10,
            [theme.breakpoints.down("sm")]: {
                marginRight: 5,
            },
        },
        title: {
            color: "black",
            fontWeight: 600,
            [theme.breakpoints.up("sm")]: {
                display: "block",
            },
        },
        btn: {
            position: "absolute",
            right: 40,
            color: "black"
        },
        btnTxt: {
            marginLeft: 5,
            fontWeight: 500
        }
    })
);
function Appbar(props: any) {
    const history = useHistory();
    const onSuccess = () => {
        history.push('/login')
    }
    const classes = useStyles();

    // session should be destroyed and user taken back to the login page
    function logoutUser() {
        fetch('https://localhost:8080/api/profile/logout', {
            method: 'GET', 
        })
        .then(() => {
            onSuccess();
        })
    }

    return (
        <AppBar position="fixed" className={classes.mainAppBar}>
            <Toolbar className={classes.root}>
                <Link to='/'>
                    <img src={Logo} className={classes.logo} />
                </Link>
                <Typography
                    className={classes.title}
                    variant="h6"
                    noWrap
                >
                    THE AFRICAN IMPACT CHALLENGE
        </Typography>
                <IconButton
                    onClick={logoutUser}
                    className={classes.btn}
                >
                    <InputIcon />
                    <Typography variant="button" noWrap className={classes.btnTxt}
                    >
                        Sign Out
            </Typography>
                </IconButton>
            </Toolbar>
        </AppBar>
    );
}

export default Appbar;
